class DataPage extends Component {

  constructor(props) {
    super(props)

    this.state = {
      name: ""
    }

    this.handleChange = this.handleChange.bind(this)
    this.clearInput = this.clearInput.bind(this)
  }

  handleChange(event) {
    event.persist()
    console.log("handle change", event)
