import React, { Component } from 'react';
import {Block, BlockTitle, List, ListInput, Page, Navbar} from 'framework7-react'

class DataPage extends Component {

  constructor(props) {
    super(props)

    this.state = {
      name: ""
    }

    this.handleChange = this.handleChange.bind(this)
  }

  getInput(event) {
    console.log("GET INPUT", event)
  }

  handleChange(event) {
    event.persist()
    console.log("handle change", event)
    this.setState({[event.target.name]: event.target.value})
  }

  clearInput(event) {
    event.persist()
    console.log("clear input")
    this.setState({[event.target.name]: ""})
  }

  render() {
    console.log("STAET", this.state)
    return (
      <Page>
        <Navbar title="About" backLink="Back"></Navbar>
          <BlockTitle>Your params</BlockTitle>
          <List inset>
            <ListInput
              label="Name 1"
              type="text"
              placeholder="Your name"
              name="name"
              clearButton
              onChange={this.handleChange}
              value={this.state.name}
              clearButton={true}
              onInputClear={this.clearInput}
            />
            <ListInput
              label="2 Something else"
              type="text"
              placeholder="Not name"
              clearButton
            />
          </List>
      </Page>
    );
  }

}

export default DataPage;
