import { document } from 'ssr-window';
import Device from '../../utils/device';
export default {
  name: 'device',
  proto: {
    device: Device
  },
  static: {
    device: Device
  },
  on: {
    init: function init() {
      var classNames = [];
      var html = document.querySelector('html');
      var metaStatusbar = document.querySelector('meta[name="apple-mobile-web-app-status-bar-style"]');
      if (!html) return;

      if (Device.standalone && Device.ios && metaStatusbar && metaStatusbar.content === 'black-translucent') {
        classNames.push('device-full-viewport');
      } // Pixel Ratio


      classNames.push("device-pixel-ratio-".concat(Math.floor(Device.pixelRatio)));

      if (Device.pixelRatio >= 2) {
        classNames.push('device-retina');
      } // OS classes


      if (Device.os) {
        classNames.push("device-".concat(Device.os), "device-".concat(Device.os, "-").concat(Device.osVersion.split('.')[0]), "device-".concat(Device.os, "-").concat(Device.osVersion.replace(/\./g, '-')));

        if (Device.os === 'ios') {
          var major = parseInt(Device.osVersion.split('.')[0], 10);

          for (var i = major - 1; i >= 6; i -= 1) {
            classNames.push("device-ios-gt-".concat(i));
          }

          if (Device.iphoneX) {
            classNames.push('device-iphone-x');
          }
        }
      } else if (Device.desktop) {
        classNames.push('device-desktop');
        if (Device.macos) classNames.push('device-macos');else if (Device.windows) classNames.push('device-windows');
      }

      if (Device.cordova || Device.phonegap) {
        classNames.push('device-cordova');
      } // Add html classes


      classNames.forEach(function (className) {
        html.classList.add(className);
      });
    }
  }
};
