import React, { Component } from 'react';
import {Block, BlockTitle, List, ListInput, Page, Navbar} from 'framework7-react'
import { Line } from 'react-chartjs-2';

const graphOptions = {
  maintainAspectRatio: false, //this will make the graph use assigned size
  scales: {
      yAxes: [{
          ticks: {
              beginAtZero:false
          }
      }]
  },
  legend: {
    display: false
  },
  tooltips: {
    enabled: true,
    backgroundColor: 'rgba(255,255,255,1)',
    titleFontColor: '#000',
    titleFontSize: 16,
    bodyFontColor: '#000',
    bodyFontSize: 16,
    displayColors: false,
    borderColor: '#000',
    borderWidth: 1
  },
  annotation: {
    annotations: [{
      //drawTime: "afterDatasetsDraw",
      id: "hline",
      type: "line",
      mode: "horizontal",
      scaleID: "y-axis-0",
      value: 80.0,
      borderColor: "black",
      borderWidth: 1,
      borderDash: [2, 2],
    label: {
      backgroundColor: "white",
      content: "Goal Weight",
      enabled: true,
      fontColor: "black"
      }

  }]
}
}

class DataPage extends Component {

  constructor(props) {
    super(props)

    this.state = {
      name: ""
    }

    this.handleChange = this.handleChange.bind(this)
    this.clearInput = this.clearInput.bind(this)
  }

  handleChange(event) {
    event.persist()
    console.log("handle change", event)
    this.setState({[event.target.name]: event.target.value})
  }

  clearInput(event) {
    console.log("clear input", event)
    this.setState({[event.target.name]: ""})
  }

  render() {
    console.log("DATA PROPS", this.props.data)
    return (
      <Page>
        <Navbar title="Results" backLink="Back"></Navbar>
        <Block>
        <Line
            data={this.props.data}
            options={graphOptions}
          />
      </Block>
          <BlockTitle>Your stats</BlockTitle>
          <List inset>
            <ListInput
              label="Current weight"
              type="number"
              placeholder=""
              name="weight"
              clearButton
              onChange={this.handleChange}
              value={this.state.weight}
              clearButton={true}
              onInputClear={this.clearInput}
            />
            <ListInput
              label="Age"
              type="number"
              placeholder=""
              name="age"
              clearButton
              onChange={this.handleChange}
              value={this.state.age}
              clearButton={true}
              onInputClear={this.clearInput}
            />
          </List>
      </Page>
    );
  }

}

export default DataPage;
