import React, { Component } from 'react';
import {
    Page,
    Navbar,
    NavLeft,
    NavTitle,
    NavRight,
    Link,
    Toolbar,
    Block,
    BlockTitle,
    List,
    ListItem,
    ListInput,
    Row,
    Col,
    Button
} from 'framework7-react';

class HomePage extends Component {

  constructor(props) {
    super(props)
      this.state = {}

      this.handleChange = this.handleChange.bind(this)
      this.clearInput = this.clearInput.bind(this)
      this.calculate = this.calculate.bind(this)
      this.calculateWeightLoss = this.calculateWeightLoss.bind(this)
      this.addDays = this.addDays.bind(this)
      this.calculateTDEE = this.calculateTDEE.bind(this)
      this.parseMonth = this.parseMonth.bind(this)

  }

  handleChange(event) {
    event.persist()
    this.setState({[event.target.name]: event.target.value})
  }

  clearInput(event) {
    this.setState({[event.target.name]: ""})
  }

  calculate() {
    //do input checks first
    this.calculateWeightLoss()
  }

  //Adds specified number of days to current date
  addDays(days) {
    // date.setDate(date.getDate() + days);
  }

  calculateTDEE() {
    return 2000;
  }

  parseMonth(month) {
    switch (month) {
      case 0: return "Jan";
      break;

      case 1: return "Feb";
      break;

      case 2: return "Mar";
      break;

      case 3: return "Apr";
      break;

      case 4: return "May";
      break;

      case 5: return "Jun";
      break;

      case 6: return "Jul";
      break;

      case 7: return "Aug";
      break;

      case 8: return "Sep";
      break;

      case 9: return "Oct";
      break;

      case 10: return "Nov";
      break;

      case 11: return "Dec";
      break;

      default: return "N/A";
      break;

    }
  }

  calculateWeightLoss() {
    console.log("calculate weight loss")
    const labels = []
    const data = []
    const date = new Date()
    console.log("DATE", date)

    let weight = parseFloat(this.state.weight)
    let age = this.state.age

    console.log("WEIGHT", weight)

    //these do 1 time per month
    labels.push(this.parseMonth(date.getMonth()) + " " + date.getFullYear());
    data.push(weight.toFixed(1));

    while(parseFloat(weight) >= parseFloat(this.state.goal-3)) {

      console.log("WHILE LOOP", weight)

      console.log("calculateTDEE", this.calculateTDEE())
      console.log("intake", this.state.intake)

      // calculate caloric deficit by subtracting intake from TDEE
      let deficit = this.calculateTDEE() - this.state.intake;
      console.log("DEFICIT", deficit)
      // calculate deficit in KG by dividing calories/7700
      let kgDeficit = deficit/7700;
      console.log("KG DEFICIT", kgDeficit)
      // set new weight after subtracting deficit
      weight = weight - kgDeficit;
      console.log("WEIGHT 2", weight)
      // add a day?
      this.addDays(1);
      // set age one day higher
      age = age + 1/365;

      //on the 14th day of each month add data to arrays that will be displayed on chart
      if(date.getDate() == 14) {
        labels.push(this.parseMonth(date.getMonth()) + " " + date.getFullYear());
        data.push(weight.toFixed(1));
      }

      if (this.calculateTDEE() <= this.state.intake) {
        // myApp.alert("You can't reach your goal weight with given calorie intake and level of activity. The lowest weight you can achieve is: " + weight.toFixed(1), "Oops!");
        // weight = 0; //this needs to be here to break out of while loop
    	}

        }


  }

render() {
  console.log("STATE", this.state)
  return (
    <Page>
      <Navbar>
        {/*<NavLeft>
          <Link iconIos="f7:menu" iconMd="material:menu" panelOpen="left"></Link>
        </NavLeft>*/}
        <NavTitle>Weight loss estimator</NavTitle>
        {/*<NavRight>
          <Link iconIos="f7:menu" iconMd="material:menu" panelOpen="right"></Link>
        </NavRight>*/}
      </Navbar>
      {/*<Toolbar bottom>
        <Link>Left Link</Link>
        <Link>Right Link</Link>
      </Toolbar>*/}



      <BlockTitle>Your params</BlockTitle>
        <List inset>
          <ListInput
            label="Current weight (kg)"
            type="number"
            placeholder="105 kg"
            name="weight"
            clearButton
            onChange={this.handleChange}
            value={this.state.weight}
            clearButton={true}
            onInputClear={this.clearInput}
          />
          <ListInput
            label="Age"
            type="number"
            placeholder="29"
            name="age"
            clearButton
            onChange={this.handleChange}
            value={this.state.age}
            clearButton={true}
            onInputClear={this.clearInput}
          />
          <ListInput
            label="Height (cm)"
            type="number"
            placeholder="179"
            name="height"
            clearButton
            onChange={this.handleChange}
            value={this.state.height}
            clearButton={true}
            onInputClear={this.clearInput}
          />
          <ListInput
            label="Goal weight (kg)"
            type="number"
            placeholder="80"
            name="goal"
            clearButton
            onChange={this.handleChange}
            value={this.state.goal}
            clearButton={true}
            onInputClear={this.clearInput}
          />
          <ListInput
            label="Daily intake (kcal)"
            type="number"
            placeholder="2100"
            name="intake"
            clearButton
            onChange={this.handleChange}
            value={this.state.intake}
            clearButton={true}
            onInputClear={this.clearInput}
          />
          <ListInput
            label="Gender"
            type="select"
            defaultValue="1"
            placeholder="Please choose..."
            name="gender"
            onChange={this.handleChange}
            value={this.state.gender}
            >
            <option value="1">Male</option>
            <option value="0">Female</option>
          </ListInput>
          <ListItem title="Activity level" smartSelect>
            <select
              name="activity"
              defaultValue="1"
              onChange={this.handleChange}
              value={this.state.activity}
              >
              <option value="1">Couch potato</option>
              <option value="2">Light activity</option>
              <option value="3">Moderate activity</option>
              <option value="4">Heavy activity</option>
              <option value="5">Athlete</option>
            </select>
          </ListItem>
        </List>

        <Block strong inset>
          <p>Click calculate to see the results.</p>
        </Block>

      <Block>
        <Row>
          <Col>
            <Button fill round large onClick={this.calculate}>Calculate</Button>
          </Col>
        </Row>
        <Row>
          <Col>
            <Button href={"/about/"} style={{marginTop: "15px"}}>About</Button>
          </Col>
        </Row>
      </Block>

      <BlockTitle>Navigation</BlockTitle>
      <List>
        <ListItem link="/about/" title="About"></ListItem>
        <ListItem link="/form/" title="Form"></ListItem>
        <ListItem link="/data/" title="Data"></ListItem>
      </List>
      <BlockTitle>Modals</BlockTitle>
      <Block strong>
        <Row>
          <Col width="50">
            <Button fill raised popupOpen="#popup">Popup</Button>
          </Col>
          <Col width="50">
            <Button fill raised loginScreenOpen="#login-screen">Login Screen</Button>
          </Col>
        </Row>
      </Block>
      <BlockTitle>Panels</BlockTitle>
      <Block strong>
        <Row>
          <Col width="50">
            <Button fill raised panelOpen="left">Left Panel</Button>
          </Col>
          <Col width="50">
            <Button fill raised panelOpen="right">Right Panel</Button>
          </Col>
        </Row>
      </Block>
      <List>
        <ListItem link="/dynamic-route/blog/45/post/125/?foo=bar#about" title="Dynamic Route"></ListItem>
